mapper.select:
select * from (SELECT a.id, b.entity_name, b.scount, b.execute_time, a.entity_id, CASE WHEN c.config > 0 THEN 1 ELSE 0 END AS config FROM zs_overview a INNER JOIN zs_entity b ON a.entity_id = b.id LEFT JOIN ( SELECT b.overview_id, count(b.overview_id) AS config FROM zs_overview_param b WHERE b.search_field = '0' OR b.dic_id IS NOT NULL GROUP BY b.overview_id ) c ON a.id = c.overview_id WHERE a. ENABLE = '0' and a.subject_id = #{overview.subject_id}) a where 1 =1
and entity_name like concat('%',#{overview.entity_name},'%')
and config = #{overview.config}
limit #{page.start} , #{page.length}

select count(*) from ( SELECT a.id, b.entity_name, b.scount, b.execute_time, a.entity_id, CASE WHEN c.config > 0 THEN 1 ELSE 0 END AS config FROM zs_overview a INNER JOIN zs_entity b ON a.entity_id = b.id LEFT JOIN ( SELECT b.overview_id, CASE WHEN count(b.overview_id) > 0 THEN 1 ELSE 0 END AS config FROM zs_overview_param b WHERE b.search_field = '0' OR b.dic_id IS NOT NULL GROUP BY b.overview_id ) c ON a.id = c.overview_id WHERE a.enable = '0' and a.subject_id = #{subject_id}
) a where 1=1
and entity_name like concat('%',#{entity_name},'%')
and config = #{config}
